import { useEffect, useState } from "react";

function AdminPanel() {
    const [funcoes, setFuncoes] = useState([]);
    const [permissoesAdmin, setPermissoesAdmin] = useState([]);
    const [permissoesJogador, setPermissoesJogador] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8080/admin/funcoes")
            .then(response => response.json())
            .then(data => setFuncoes(data));

        fetch("http://localhost:8080/admin/permissoes/1")
            .then(response => response.json())
            .then(data => setPermissoesAdmin(data.map(p => p.funcao.codigo)));

        fetch("http://localhost:8080/admin/permissoes/2")
            .then(response => response.json())
            .then(data => setPermissoesJogador(data.map(p => p.funcao.codigo)));
    }, []);

    const atualizarPermissao = (codPerfil, codFuncao, permitir) => {
        fetch(`http://localhost:8080/admin/permissoes/atualizar?codPerfil=${codPerfil}&codFuncao=${codFuncao}&permitir=${permitir}`, {
            method: "POST",
        }).then(() => {
            if (codPerfil === 1) {
                setPermissoesAdmin(prev => permitir ? [...prev, codFuncao] : prev.filter(c => c !== codFuncao));
            } else {
                setPermissoesJogador(prev => permitir ? [...prev, codFuncao] : prev.filter(c => c !== codFuncao));
            }
        });
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>

            <h3>Permiss√µes do Administrador</h3>
            <table>
                <tbody>
                    {funcoes.map(funcao => (
                        <tr key={funcao.codigo}>
                            <td>{funcao.descricao}</td>
                            <td>
                                <input type="checkbox" checked={permissoesAdmin.includes(funcao.codigo)}
                                    onChange={(e) => atualizarPermissao(1, funcao.codigo, e.target.checked)} />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AdminPanel;
