import { useEffect, useState } from "react";

function AdminPanel() {
    const [permissoes, setPermissoes] = useState([]);
    const [funcoes, setFuncoes] = useState([]);
    const [perfilSelecionado, setPerfilSelecionado] = useState(1); // 1 = Administrador, 2 = Jogador

    // ðŸ”¹ Buscar permissÃµes e funÃ§Ãµes ao carregar a pÃ¡gina
    useEffect(() => {
        fetch("http://localhost:8080/admin/permissoes")
            .then(response => response.json())
            .then(data => setPermissoes(data));

        fetch("http://localhost:8080/admin/funcoes")
            .then(response => response.json())
            .then(data => setFuncoes(data));
    }, []);

    // ðŸ”¹ Verifica se o perfil tem permissÃ£o para a funÃ§Ã£o
    const temPermissao = (codFuncao) => {
        return permissoes.some(p => p.perfil.codigo === perfilSelecionado && p.funcao.codigo === codFuncao);
    };

    // ðŸ”¹ Alternar permissÃ£o ao clicar no checkbox
    const togglePermissao = (codFuncao) => {
        const possuiPermissao = temPermissao(codFuncao);

        if (possuiPermissao) {
            fetch(`http://localhost:8080/admin/remover?codPerfil=${perfilSelecionado}&codFuncao=${codFuncao}`, {
                method: "DELETE",
            }).then(() => atualizarPermissoes());
        } else {
            fetch(`http://localhost:8080/admin/atribuir?codPerfil=${perfilSelecionado}&codFuncao=${codFuncao}`, {
                method: "POST",
            }).then(() => atualizarPermissoes());
        }
    };

    // ðŸ”¹ Atualizar permissÃµes apÃ³s mudanÃ§a
    const atualizarPermissoes = () => {
        fetch("http://localhost:8080/admin/permissoes")
            .then(response => response.json())
            .then(data => setPermissoes(data));
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>

            {/* ðŸ”¹ Selecionar Perfil */}
            <label>Perfil: </label>
            <select value={perfilSelecionado} onChange={(e) => setPerfilSelecionado(parseInt(e.target.value))}>
                <option value={1}>Administrador</option>
                <option value={2}>Jogador</option>
            </select>

            <h3>PermissÃµes de {perfilSelecionado === 1 ? "Administrador" : "Jogador"}</h3>

            {/* ðŸ”¹ Tabela de PermissÃµes */}
            <table border="1">
                <thead>
                    <tr>
                        <th>FunÃ§Ã£o</th>
                        <th>PermissÃ£o</th>
                    </tr>
                </thead>
                <tbody>
                    {funcoes.map(funcao => (
                        <tr key={funcao.codigo}>
                            <td>{funcao.descricao}</td>
                            <td>
                                <input
                                    type="checkbox"
                                    checked={temPermissao(funcao.codigo)}
                                    onChange={() => togglePermissao(funcao.codigo)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AdminPanel;
