import { useEffect, useState } from "react";

function AdminPanel() {
    const [permissoes, setPermissoes] = useState([]);
    const [perfilSelecionado, setPerfilSelecionado] = useState(1); // Admin como padr√£o
    const [funcoes, setFuncoes] = useState([]);

    // üîπ Busca todas as fun√ß√µes dispon√≠veis
    useEffect(() => {
        fetch("http://localhost:8080/admin/funcoes")
            .then(response => {
                if (!response.ok) throw new Error("Erro ao buscar fun√ß√µes");
                return response.json();
            })
            .then(data => setFuncoes(data))
            .catch(error => console.error("Erro ao buscar fun√ß√µes:", error));
    }, []);

    // üîπ Busca permiss√µes do perfil selecionado
    useEffect(() => {
        if (!perfilSelecionado) return;
        fetch(`http://localhost:8080/admin/permissoes?perfilId=${perfilSelecionado}`)
            .then(response => {
                if (!response.ok) throw new Error("Erro ao buscar permiss√µes");
                return response.json();
            })
            .then(data => setPermissoes(data))
            .catch(error => console.error("Erro ao buscar permiss√µes:", error));
    }, [perfilSelecionado]);

    const togglePermissao = (codFuncao, checked) => {
        const url = checked
            ? `http://localhost:8080/admin/atribuir?codPerfil=${perfilSelecionado}&codFuncao=${codFuncao}`
            : `http://localhost:8080/admin/remover?codPerfil=${perfilSelecionado}&codFuncao=${codFuncao}`;

        fetch(url, { method: checked ? "POST" : "DELETE" })
            .then(() => {
                fetch(`http://localhost:8080/admin/permissoes?perfilId=${perfilSelecionado}`)
                    .then(response => response.json())
                    .then(data => setPermissoes(data))
                    .catch(error => console.error("Erro ao atualizar permiss√µes:", error));
            })
            .catch(error => console.error("Erro ao modificar permiss√£o:", error));
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>
            <label>Perfil:</label>
            <select onChange={(e) => setPerfilSelecionado(e.target.value)} value={perfilSelecionado}>
                <option value="1">Administrador</option>
                <option value="2">Jogador</option>
            </select>

            <h3>Permiss√µes do {perfilSelecionado === "1" ? "Administrador" : "Jogador"}</h3>
            <table>
                <thead>
                    <tr>
                        <th>Fun√ß√£o</th>
                        <th>Permiss√£o</th>
                    </tr>
                </thead>
                <tbody>
                    {funcoes.length === 0 ? (
                        <tr><td colSpan="2">Nenhuma fun√ß√£o dispon√≠vel</td></tr>
                    ) : (
                        funcoes.map((funcao) => {
                            const possuiPermissao = permissoes.some((p) => p.funcao.codigo === funcao.codigo);

                            return (
                                <tr key={funcao.codigo}>
                                    <td>{funcao.descricao}</td>
                                    <td>
                                        <input
                                            type="checkbox"
                                            checked={possuiPermissao}
                                            onChange={(e) => togglePermissao(funcao.codigo, e.target.checked)}
                                        />
                                    </td>
                                </tr>
                            );
                        })
                    )}
                </tbody>
            </table>
        </div>
    );
}

export default AdminPanel;
