import { useEffect, useState } from "react";

function AdminPanel() {
    const [permissoes, setPermissoes] = useState([]);
    const [perfilSelecionado, setPerfilSelecionado] = useState(1); // ID do Administrador por padrão

    useEffect(() => {
        carregarPermissoes();
    }, [perfilSelecionado]);

    const carregarPermissoes = () => {
        fetch(`http://localhost:8080/admin/permissoes/${perfilSelecionado}`)
            .then(response => response.json())
            .then(data => {
                console.log("Permissões recebidas:", data);
                setPermissoes(data);
            })
            .catch(error => console.error("Erro ao buscar permissões:", error));
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>
            <label>Perfil:</label>
            <select value={perfilSelecionado} onChange={(e) => setPerfilSelecionado(e.target.value)}>
                <option value="1">Administrador</option>
                <option value="2">Jogador</option>
            </select>

            <h3>Permissões do {perfilSelecionado === "1" ? "Administrador" : "Jogador"}</h3>
            {permissoes.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>Função</th>
                            <th>Permissão</th>
                        </tr>
                    </thead>
                    <tbody>
                        {permissoes.map(perm => (
                            <tr key={perm.codigo}>
                                <td>{perm.funcao.descricao}</td>
                                <td>
                                    <input type="checkbox" checked={true} readOnly />
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Nenhuma função disponível</p>
            )}
        </div>
    );
}

export default AdminPanel;
