import { useEffect, useState } from "react";

function AdminPanel() {
    const [permissoes, setPermissoes] = useState([]);
    const [perfil, setPerfil] = useState(1); // Perfil padrão (Admin)

    useEffect(() => {
        fetch(`http://localhost:8080/admin/permissoes?codPerfil=${perfil}`)
            .then(response => response.json())
            .then(data => setPermissoes(data))
            .catch(error => console.error("Erro ao buscar permissões:", error));
    }, [perfil]); // Atualiza quando o perfil muda

    return (
        <div>
            <h2>Painel Administrativo</h2>
            <label>
                Perfil: 
                <select onChange={(e) => setPerfil(e.target.value)}>
                    <option value="1">Administrador</option>
                    <option value="2">Jogador</option>
                </select>
            </label>
            <h3>Permissões do {perfil === "1" ? "Administrador" : "Jogador"}</h3>
            <table>
                <thead>
                    <tr>
                        <th>Função</th>
                        <th>Permissão</th>
                    </tr>
                </thead>
                <tbody>
                    {permissoes.map(perm => (
                        <tr key={perm.funcao.codigo}>
                            <td>{perm.funcao.descricao}</td>
                            <td>
                                <input 
                                    type="checkbox" 
                                    checked={perm.ativo} 
                                    onChange={() => togglePermissao(perfil, perm.funcao.codigo, !perm.ativo)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AdminPanel;
