import { useEffect, useState } from "react";

function AdminPanel() {
    const [permissoes, setPermissoes] = useState([]);
    const [perfilSelecionado, setPerfilSelecionado] = useState(1); // 1 = Administrador, 2 = Jogador

    useEffect(() => {
        fetch("http://localhost:8080/admin/permissoes")
            .then(response => response.json())
            .then(data => setPermissoes(data));
    }, []);

    const togglePermissao = (codFuncao, checked) => {
        fetch(`http://localhost:8080/admin/togglePermissao?codPerfil=${perfilSelecionado}&codFuncao=${codFuncao}&checked=${checked}`, {
            method: "POST",
        })
        .then(() => {
            setPermissoes(permissoes.map(p =>
                p.funcao.codigo === codFuncao ? { ...p, ativa: checked } : p
            ));
        });
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>
            <label>Perfil:</label>
            <select onChange={(e) => setPerfilSelecionado(Number(e.target.value))}>
                <option value="1">Administrador</option>
                <option value="2">Jogador</option>
            </select>

            <h3>Permissões do {perfilSelecionado === 1 ? "Administrador" : "Jogador"}</h3>
            <table border="1">
                <thead>
                    <tr>
                        <th>Função</th>
                        <th>Permissão</th>
                    </tr>
                </thead>
                <tbody>
                    {permissoes
                        .filter(p => p.perfil.codigo === perfilSelecionado)
                        .map(perm => (
                            <tr key={perm.funcao.codigo}>
                                <td>{perm.funcao.descricao}</td>
                                <td>
                                    <input
                                        type="checkbox"
                                        checked={perm.ativa || false}
                                        onChange={(e) => togglePermissao(perm.funcao.codigo, e.target.checked)}
                                    />
                                </td>
                            </tr>
                        ))}
                </tbody>
            </table>
        </div>
    );
}

export default AdminPanel;
