import { useEffect, useState } from "react";

function AdminPanel() {
    const [perfis, setPerfis] = useState([
        { codigo: 1, nome: "Administrador" },
        { codigo: 2, nome: "Jogador" }
    ]);
    const [perfilSelecionado, setPerfilSelecionado] = useState(1);
    const [funcoes, setFuncoes] = useState([]);
    const [permissoes, setPermissoes] = useState(new Set());

    // üîπ Carrega todas as fun√ß√µes dispon√≠veis e permiss√µes do perfil selecionado
    useEffect(() => {
        fetch(`http://localhost:8080/admin/funcoes`)
            .then(response => response.json())
            .then(data => setFuncoes(data));

        fetch(`http://localhost:8080/admin/permissoes?codPerfil=${perfilSelecionado}`)
            .then(response => response.json())
            .then(data => {
                const permissoesAtivas = new Set(data.map(perm => perm.funcao.codigo));
                setPermissoes(permissoesAtivas);
            });
    }, [perfilSelecionado]);

    // üîπ Alterna permiss√µes ao clicar no checkbox
    const togglePermissao = (funcaoId) => {
        const novasPermissoes = new Set(permissoes);
        if (novasPermissoes.has(funcaoId)) {
            novasPermissoes.delete(funcaoId);
        } else {
            novasPermissoes.add(funcaoId);
        }
        setPermissoes(novasPermissoes);
    };

    // üîπ Envia as permiss√µes atualizadas ao backend
    const confirmarAlteracoes = () => {
        fetch(`http://localhost:8080/admin/atualizar-permissoes?codPerfil=${perfilSelecionado}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(Array.from(permissoes))
        }).then(() => alert("Permiss√µes atualizadas com sucesso!"));
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>
            <label>Perfil: </label>
            <select onChange={e => setPerfilSelecionado(e.target.value)} value={perfilSelecionado}>
                {perfis.map(perfil => (
                    <option key={perfil.codigo} value={perfil.codigo}>{perfil.nome}</option>
                ))}
            </select>

            <h3>Permiss√µes do {perfis.find(p => p.codigo == perfilSelecionado)?.nome}</h3>
            <table border="1">
                <thead>
                    <tr>
                        <th>Fun√ß√£o</th>
                        <th>Permiss√£o</th>
                    </tr>
                </thead>
                <tbody>
                    {funcoes.map(funcao => (
                        <tr key={funcao.codigo}>
                            <td>{funcao.descricao}</td>
                            <td>
                                <input
                                    type="checkbox"
                                    checked={permissoes.has(funcao.codigo)}
                                    onChange={() => togglePermissao(funcao.codigo)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={confirmarAlteracoes}>Confirmar</button>
        </div>
    );
}

export default AdminPanel;
