import { useEffect, useState } from "react";

function AdminPanel() {
    const [permissoes, setPermissoes] = useState([]);
    const [perfil, setPerfil] = useState("1"); // 1 = Administrador, 2 = Jogador
    const [alteracoes, setAlteracoes] = useState({}); // Armazena alterações temporárias

    // Busca permissões do banco ao mudar o perfil
    useEffect(() => {
        fetch(`http://localhost:8080/admin/permissoes?codPerfil=${perfil}`)
            .then(response => response.json())
            .then(data => {
                setPermissoes(data);
                const estadoInicial = {};
                data.forEach(perm => {
                    estadoInicial[perm.funcao.codigo] = true; // Marca checkboxes corretamente
                });
                setAlteracoes(estadoInicial);
            })
            .catch(error => console.error("Erro ao buscar permissões:", error));
    }, [perfil]);

    // Manipula mudanças no checkbox
    const togglePermissao = (codFuncao) => {
        setAlteracoes(prev => ({
            ...prev,
            [codFuncao]: !prev[codFuncao]
        }));
    };

    // Envia mudanças para o banco de dados ao confirmar
    const confirmarAlteracoes = () => {
        Object.keys(alteracoes).forEach(codFuncao => {
            const ativar = alteracoes[codFuncao]; // true = adicionar, false = remover
            const url = `http://localhost:8080/admin/${ativar ? "atribuir" : "remover"}?codPerfil=${perfil}&codFuncao=${codFuncao}`;

            fetch(url, { method: ativar ? "POST" : "DELETE" })
                .then(response => {
                    if (!response.ok) throw new Error("Erro ao atualizar permissão");
                })
                .catch(error => console.error(error));
        });

        alert("Permissões atualizadas com sucesso!");
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>
            <label>
                Perfil: 
                <select value={perfil} onChange={(e) => setPerfil(e.target.value)}>
                    <option value="1">Administrador</option>
                    <option value="2">Jogador</option>
                </select>
            </label>

            <h3>Permissões do {perfil === "1" ? "Administrador" : "Jogador"}</h3>
            <table border="1">
                <thead>
                    <tr>
                        <th>Função</th>
                        <th>Permissão</th>
                    </tr>
                </thead>
                <tbody>
                    {permissoes
                        .filter((perm, index, self) => // Remove permissões duplicadas
                            index === self.findIndex(t => t.funcao.codigo === perm.funcao.codigo)
                        )
                        .map(perm => (
                            <tr key={perm.funcao.codigo}>
                                <td>{perm.funcao.descricao}</td>
                                <td>
                                    <input
                                        type="checkbox"
                                        checked={!!alteracoes[perm.funcao.codigo]}
                                        onChange={() => togglePermissao(perm.funcao.codigo)}
                                    />
                                </td>
                            </tr>
                        ))}
                </tbody>
            </table>
            <br />
            <button onClick={confirmarAlteracoes}>Confirmar</button>
        </div>
    );
}

export default AdminPanel;
