import { useEffect, useState } from "react";

function AdminPanel() {
    const [permissoes, setPermissoes] = useState([]);
    const [funcoes, setFuncoes] = useState([]);
    const [perfilSelecionado, setPerfilSelecionado] = useState(1);
    const [permissoesSelecionadas, setPermissoesSelecionadas] = useState([]);

    // üîπ Carrega todas as fun√ß√µes dispon√≠veis
    useEffect(() => {
        fetch("http://localhost:8080/admin/funcoes")
            .then(response => response.json())
            .then(data => setFuncoes(data));
    }, []);

    // üîπ Carrega as permiss√µes do perfil selecionado
    useEffect(() => {
        fetch(`http://localhost:8080/admin/permissoes?codPerfil=${perfilSelecionado}`)
            .then(response => response.json())
            .then(data => {
                setPermissoes(data);
                setPermissoesSelecionadas(data.map(p => p.funcao.codigo));
            });
    }, [perfilSelecionado]);

    // üîπ Atualiza estado ao clicar nas checkboxes
    const togglePermissao = (codFuncao) => {
        setPermissoesSelecionadas(prev => {
            if (prev.includes(codFuncao)) {
                return prev.filter(id => id !== codFuncao);
            } else {
                return [...prev, codFuncao];
            }
        });
    };

    // üîπ Atualiza o banco de dados ao clicar no bot√£o "Confirmar"
    const confirmarAlteracoes = () => {
        fetch(`http://localhost:8080/admin/atualizar-permissoes?codPerfil=${perfilSelecionado}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(permissoesSelecionadas)
        }).then(() => alert("Permiss√µes atualizadas!"));
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>

            <label>Perfil: </label>
            <select onChange={(e) => setPerfilSelecionado(e.target.value)} value={perfilSelecionado}>
                <option value="1">Administrador</option>
                <option value="2">Jogador</option>
            </select>

            <h3>Permiss√µes do {perfilSelecionado === "1" ? "Administrador" : "Jogador"}</h3>

            <table>
                <thead>
                    <tr>
                        <th>Fun√ß√£o</th>
                        <th>Permiss√£o</th>
                    </tr>
                </thead>
                <tbody>
                    {funcoes.map(funcao => (
                        <tr key={funcao.codigo}>
                            <td>{funcao.descricao}</td>
                            <td>
                                <input
                                    type="checkbox"
                                    checked={permissoesSelecionadas.includes(funcao.codigo)}
                                    onChange={() => togglePermissao(funcao.codigo)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <button onClick={confirmarAlteracoes}>Confirmar</button>
        </div>
    );
}

export default AdminPanel;
