import { useEffect, useState } from "react";

function AdminPanel() {
    const [permissoes, setPermissoes] = useState([]);
    const [perfil, setPerfil] = useState(1);
    const [funcoes, setFuncoes] = useState([]);

    useEffect(() => {
        fetch(`http://localhost:8080/admin/permissoes`)
            .then(response => response.json())
            .then(data => setPermissoes(data));

        fetch(`http://localhost:8080/admin/funcoes`)
            .then(response => response.json())
            .then(data => setFuncoes(data));
    }, []);

    const togglePermissao = (funcaoId) => {
        setPermissoes(prev => {
            const existe = prev.some(p => p.funcao.codigo === funcaoId && p.perfil.codigo === perfil);
            if (existe) {
                return prev.filter(p => !(p.funcao.codigo === funcaoId && p.perfil.codigo === perfil));
            } else {
                return [...prev, { perfil: { codigo: perfil }, funcao: { codigo: funcaoId } }];
            }
        });
    };

    const confirmarPermissoes = () => {
        const codFuncoes = permissoes
            .filter(p => p.perfil.codigo === perfil)
            .map(p => p.funcao.codigo);

        fetch(`http://localhost:8080/admin/atualizar?codPerfil=${perfil}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(codFuncoes)
        }).then(() => alert("Permissões atualizadas!"));
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>
            <label>Perfil: </label>
            <select onChange={(e) => setPerfil(Number(e.target.value))}>
                <option value={1}>Administrador</option>
                <option value={2}>Jogador</option>
            </select>
            <h3>Permissões do {perfil === 1 ? "Administrador" : "Jogador"}</h3>
            <table border="1">
                <thead>
                    <tr>
                        <th>Função</th>
                        <th>Permissão</th>
                    </tr>
                </thead>
                <tbody>
                    {funcoes.map(funcao => (
                        <tr key={funcao.codigo}>
                            <td>{funcao.descricao}</td>
                            <td>
                                <input
                                    type="checkbox"
                                    checked={permissoes.some(p => p.funcao.codigo === funcao.codigo && p.perfil.codigo === perfil)}
                                    onChange={() => togglePermissao(funcao.codigo)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={confirmarPermissoes}>Confirmar</button>
        </div>
    );
}

export default AdminPanel;
