import { useEffect, useState } from "react";

function AdminPanel() {
    const [funcoes, setFuncoes] = useState([]); // Lista de todas as fun√ß√µes poss√≠veis
    const [permissoes, setPermissoes] = useState([]); // Lista de permiss√µes atribu√≠das
    const [perfilSelecionado, setPerfilSelecionado] = useState(1); // ID do Administrador por padr√£o
    const [permissoesSelecionadas, setPermissoesSelecionadas] = useState({}); // Estado das permiss√µes

    // üîπ Carregar TODAS as fun√ß√µes dispon√≠veis (para exibir todas na tabela)
    useEffect(() => {
        fetch("http://localhost:8080/admin/funcoes") // Endpoint que retorna todas as fun√ß√µes poss√≠veis
            .then(response => response.json())
            .then(data => setFuncoes(data))
            .catch(error => console.error("Erro ao buscar fun√ß√µes:", error));
    }, []);

    // üîπ Carregar as permiss√µes do perfil selecionado
    useEffect(() => {
        fetch(`http://localhost:8080/admin/permissoes/${perfilSelecionado}`)
            .then(response => response.json())
            .then(data => {
                // Criar um mapa com as permiss√µes que o perfil possui
                const permissoesMap = {};
                data.forEach(perm => {
                    permissoesMap[perm.funcao.codigo] = true;
                });
                setPermissoesSelecionadas(permissoesMap);
                setPermissoes(data);
            })
            .catch(error => console.error("Erro ao buscar permiss√µes:", error));
    }, [perfilSelecionado]);

    // üîπ Atualiza o estado ao clicar no checkbox
    const togglePermissao = (codFuncao) => {
        setPermissoesSelecionadas(prev => ({
            ...prev,
            [codFuncao]: !prev[codFuncao]
        }));
    };

    // üîπ Enviar altera√ß√µes para o backend
    const confirmarAlteracoes = () => {
        const permissoesAtivas = Object.keys(permissoesSelecionadas).filter(cod => permissoesSelecionadas[cod]);

        fetch(`http://localhost:8080/admin/atualizar-permissoes?codPerfil=${perfilSelecionado}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(permissoesAtivas)
        })
        .then(() => alert("Permiss√µes atualizadas com sucesso!"))
        .catch(error => console.error("Erro ao atualizar permiss√µes:", error));
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>
            <label>Perfil:</label>
            <select value={perfilSelecionado} onChange={(e) => setPerfilSelecionado(e.target.value)}>
                <option value="1">Administrador</option>
                <option value="2">Jogador</option>
            </select>

            <h3>Permiss√µes do {perfilSelecionado === "1" ? "Administrador" : "Jogador"}</h3>
            {funcoes.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>Fun√ß√£o</th>
                            <th>Permiss√£o</th>
                        </tr>
                    </thead>
                    <tbody>
                        {funcoes.map(funcao => (
                            <tr key={funcao.codigo}>
                                <td>{funcao.descricao}</td>
                                <td>
                                    <input
                                        type="checkbox"
                                        checked={!!permissoesSelecionadas[funcao.codigo]}
                                        onChange={() => togglePermissao(funcao.codigo)}
                                    />
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Carregando fun√ß√µes...</p>
            )}

            <button onClick={confirmarAlteracoes}>Confirmar</button>
        </div>
    );
}

export default AdminPanel;
