import { useEffect, useState } from "react";

function AdminPanel() {
    const [permissoes, setPermissoes] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8080/admin/permissoes")
            .then(response => response.json())
            .then(data => setPermissoes(data));
    }, []);

    const atribuirPermissao = (codPerfil, codFuncao) => {
        fetch(`http://localhost:8080/admin/permissoes/atribuir?codPerfil=${codPerfil}&codFuncao=${codFuncao}`, {
            method: "POST",
        }).then(() => window.location.reload());
    };

    const removerPermissao = (codPerfil, codFuncao) => {
        fetch(`http://localhost:8080/admin/permissoes/remover?codPerfil=${codPerfil}&codFuncao=${codFuncao}`, {
            method: "DELETE",
        }).then(() => window.location.reload());
    };

    return (
        <div>
            <h2>Painel Administrativo</h2>
            <table>
                <thead>
                    <tr>
                        <th>Perfil</th>
                        <th>Permissões</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {permissoes.map(perm => (
                        <tr key={perm.id}>
                            <td>{perm.perfil.nome}</td>
                            <td>{perm.funcao.descricao}</td>
                            <td>
                                <button onClick={() => removerPermissao(perm.perfil.codigo, perm.funcao.codigo)}>Remover</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <h3>Atribuir Permissão</h3>
            <input type="text" id="perfil" placeholder="ID Perfil" />
            <input type="text" id="funcao" placeholder="ID Função" />
            <button onClick={() => {
                const codPerfil = document.getElementById("perfil").value;
                const codFuncao = document.getElementById("funcao").value;
                atribuirPermissao(codPerfil, codFuncao);
            }}>
                Adicionar Permissão
            </button>
        </div>
    );
}

export default AdminPanel;
